let JSONC_to_JSON = i => { let s = 0, o = ""; for (let j = 0; j < i.length; j++) { let q = i[j]; if (s == 1) { q == "\"" ? s = 0 : 0; o += q; continue } if (s == 2) { q == "\n" ? (s = 0, o += q) : 0; continue } if (s == 3) { q + i[j + 1] == "*/" ? (s = 0, j += 1) : 0; continue } if (q == "\\") { o += q, j += 1, o += i[j]; continue } if (q == "/") { i[j + 1] == "/" ? (s = 2, j += 1) : 0; i[j + 1] == "*" ? (s = 3, j += 1) : 0; continue } if (q == "\"") { (s = 1, o += q); continue } o += q; continue } return o }